import { memo } from "react";
import Carousel from "react-multi-carousel";
import 'react-multi-carousel/lib/styles.css';
// import cart1 from '../../../asset/users/images/hero/herobanner.png';
import cart1 from "../../../asset/users/images/categories/giay1.jpg";
import cart2 from "../../../asset/users/images/categories/giay2.jpeg";
import cart3 from "../../../asset/users/images/categories/giay3.jpeg";
import "./style.scss";

const homePage = () => {
  const responsive = {
    superLargeDesktop: {
      // the naming can be any, depends on you.
      breakpoint: { max: 4000, min: 3000 },
      items: 5
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 4
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  const sliderItem = [
    {
      bgImg: cart1,
      name: "Thể thao"
    },
    {
      bgImg: cart2,
      name: "Thể thao"
    },
    {
      bgImg: cart3,
      name: "Thể thao"
    },
    {
      bgImg: cart1,
      name: "Thể thao"
    },
    {
      bgImg: cart3,
      name: "Thể thao"
    },
    {
      bgImg: cart3,
      name: "Thể thao"
    },
  ]

  return (
    <div style={{marginTop: 30}}>
      {/*CategoriesBegin*/}
      <div className="container container_categories_slider">
        <Carousel responsive={responsive} className="categories_slider">
          {sliderItem.map((item, key) => (
            <div className="categories_slider_item" 
              style={{backgroundImage: `url(${item.bgImg})`}}
              key={key}
              >
                <p>{item.name}</p>
            </div>
          ))}
        </Carousel>
      </div>
      {/* Featured */}
      <div className=""></div>
    </div>
  )
};

export default memo(homePage);
